import React, { useEffect, useMemo, useRef, useState } from "react";
import styled from "styled-components";
import Modal from "@/component/common/Modal";
import { Div } from "@/styles/BaseStyledTags";
import MapSelectedLoactionModal from "./MapSelectedLoactionModal";
import { useTranslation } from "react-i18next";

interface MyRoadMapProps {
  isOpen: boolean;
  onClose: () => void;
}

interface Location {
  id: number;
  lat: number;
  lng: number;
  title: string;
  description: string;
  image: string;
}

export default function MyRoadMap({ isOpen, onClose }: MyRoadMapProps) {
  const { t } = useTranslation();
  const dummyLocation = useMemo<Location[]>(
    () => [
      {
        id: 1,
        lat: 37.795236,
        lng: 128.896123,
        title: "경포해변",
        description:
          "강릉을 대표하는 해변으로 여름철 피서지로 유명해요.\n맑은 바다와 하얀 모래사장이 펼쳐져 있습니다.",
        image:
          "data:image/jpeg;base64,",
      },
      {
        id: 2,
        lat: 37.769985,
        lng: 128.899088,
        title: "안목해변 커피거리",
        description:
          "해변을 따라 카페들이 늘어서 있어 커피 한 잔과 함께 바다를 즐기기 좋아요.\n특히 노을 질 때 분위기가 환상적입니다.",
        image:
          "https://access.visitkorea.or.kr/bfvk_img/call?cmd=VIEW&id=affc63f0-5125-46c2-9996-b3bf1a9ae74a&",
      },
      {
        id: 3,
        lat: 37.751646,
        lng: 128.876057,
        title: "오죽헌",
        description:
          "신사임당과 율곡 이이가 태어난 곳으로 잘 알려져 있어요.\n역사와 자연이 함께하는 고즈넉한 공간입니다.",
        image:
          "data:image/jpeg;base64,",
      },
      {
        id: 4,
        lat: 37.690516,
        lng: 128.905314,
        title: "정동진",
        description:
          "일출 명소로 유명하며 드라마 촬영지로도 자주 등장했어요.\n바다와 기차가 맞닿는 독특한 풍경이 인상적입니다.",
        image:
          "data:image/jpeg;base64,",
      },
      {
        id: 5,
        lat: 37.770893,
        lng: 128.861084,
        title: "초당두부마을",
        description:
          "강릉 특산물인 초당두부를 맛볼 수 있는 식당들이 모여 있어요.\n전통 방식으로 만든 담백한 두부 요리를 즐길 수 있습니다.",
        image:
          "https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=ea0832f0-6c86-4f85-9f92-6e6c875c0e18",
      },
    ],
    []
  );

  const mapRef = useRef<HTMLDivElement>(null);
  const [selectedLocation, setSelectedLocation] = useState<Location | null>(
    null
  );

  useEffect(() => {
    if (!isOpen || !mapRef.current || !window.kakao) return;

    window.kakao.maps.load(() => {
      const container = mapRef.current!;
      const options = {
        center: new window.kakao.maps.LatLng(
          dummyLocation[0].lat,
          dummyLocation[0].lng
        ),
        level: 7,
      };
      const map = new window.kakao.maps.Map(container, options);

      // 커스텀 숫자 오버레이만 생성
      dummyLocation.forEach((loc, index) => {
        const position = new window.kakao.maps.LatLng(loc.lat, loc.lng);

        // 숫자가 들어간 CustomOverlay (클릭 가능)
        const content = `<div onclick="window.selectLocation(${loc.id})" style="
            background:#ff5a5f;
            color:#fff;
            border-radius:50%;
            width:28px;
            height:28px;
            display:flex;
            justify-content:center;
            align-items:center;
            font-size:14px;
            font-weight:bold;
            box-shadow:0 2px 6px rgba(0,0,0,0.3);
            cursor:pointer;
          ">${index + 1}</div>`;

        const overlay = new window.kakao.maps.CustomOverlay({
          content,
          position,
          yAnchor: 1,
        });
        overlay.setMap(map);

        // 전역 함수로 클릭 핸들러 등록
        window.selectLocation = (locationId: number) => {
          const selectedLoc = dummyLocation.find((l) => l.id === locationId);
          if (selectedLoc) {
            setSelectedLocation(selectedLoc);
          }
        };
      });

      // 점선으로 마커들 연결
      const pathCoordinates = dummyLocation.map(
        (loc) => new window.kakao.maps.LatLng(loc.lat, loc.lng)
      );

      const polyline = new window.kakao.maps.Polyline({
        path: pathCoordinates,
        strokeWeight: 3,
        strokeColor: "#ff5a5f",
        strokeOpacity: 0.8,
        strokeStyle: "shortdash", // 점선 스타일
      });

      polyline.setMap(map);

      setTimeout(() => {
        window.kakao.maps.event.trigger(map, "resize");
      }, 0);
    });
  }, [isOpen, dummyLocation]);

  return (
    <>
      <Modal
        isOpen={isOpen}
        onClose={onClose}
        title={t("myroad.map")}
        width="100%"
        height="100%"
        maxWidth="780px"
      >
        <MapContainer ref={mapRef} />
      </Modal>

      {/* 장소 상세 모달 */}
      {selectedLocation && (
        <MapSelectedLoactionModal
          selectedLocation={selectedLocation}
          setSelectedLocation={setSelectedLocation}
        />
      )}
    </>
  );
}

const MapContainer = styled(Div)`
  width: 100%;
  height: 100%;
  border-radius: 12px;
  overflow: hidden;
`;
